      /**
      * Перемешать Список
      * Перемешать все элементы массив в случайном порядке.
      * @param {Array} arr Массив
      * @returns Array
      */
      function listShuffle(arr) {
      const arr = _function_argument('arr') || arr
      var j, x, i
      for (i = arr.length; i; i--) {
      j = Math.floor(Math.random() * i)
      x = arr[i - 1]
      arr[i - 1] = arr[j]
      arr[j] = x
      }
      _function_return(arr)
      return arr
      }
