      _SELECT_INDEX = JSON.parse(JSON.parse(_result()).variables)["RESULT"]
      _call(_random_point, {})!
      _if(_result().length > 0 && _SELECT_INDEX >= 0, function(){
      move({})!
      if !(!noWait && stopThread) {
      get_element_selector(_SELECTOR, false).clarify(X,Y)!
