      function BAS_random(a, b) {
      const par_a = _function_argument('a')
      const par_b = _function_argument('b')
      a = par_a
      b = par_b
      _function_return(Math.floor(Math.random() * b - a + 1) + a)
      // return Math.floor(Math.random() * b - a + 1) + a
      }
